mapper.select:
SELECT SEQ, NAME, LATITIUDE, LONGITUDE, ADDRESS, SI, GUNGU, FLOOR, OPEN_TIME, CLOSE_TIME, REG_DT, MOD_DT, MANAGE_AGENCY, MA_NUM, TOILET_TYPE, COUNT_MAN, COUNT_WOMEN, BABY_YN, UNUSUAL_YN, CCTV_YN, ALARM_YN, PWD_YN, PWD, ETC, REG_ID, MOD_ID, OPEN_YN FROM 120BPM_TOILET
and GUNGU = #{gungu}
AND NAME LIKE CONCAT('%', #{searchWord}, '%')
order by SEQ asc limit #{index}, #{count};

SELECT count(*) totalCount FROM 120BPM_TOILET
and GUNGU = #{gungu}
AND NAME LIKE CONCAT('%', #{searchWord}, '%')
